function mostrarAlertSwal(n="",t="",i="",r="info"){Swal.fire({icon:r,title:n,text:t,footer:i})}function mostrarAlertToast(n="",t="info"){Toast.fire({icon:t,title:n})}function alertaConfirmada(){return Swal.mixin({customClass:{confirmButton:"btn btn-success",cancelButton:"btn btn-danger"},buttonsStyling:!0})}const Toast=Swal.mixin({toast:!0,position:"top-end",showConfirmButton:!1,timer:3e3,timerProgressBar:!0,didOpen:n=>{n.addEventListener("mouseenter",Swal.stopTimer),n.addEventListener("mouseleave",Swal.resumeTimer)}});